import { useState } from 'react';
import { Link } from 'react-router-dom';
import { Button } from '@mui/material';
import styled from 'styled-components';
import logo1 from './Logo1.png';
import back from './Back.png';

const LoginContainer = styled.div`
  position: relative;
  text-align: center;
`;

const BackButton = styled.img`
  position: absolute;
  left: 20px;
  top: 20px;
`;

const Logo = styled.img`
  width: 300px;
  height: 300px;
  animation: rotate 4s infinite linear;
  display: block;
  margin: 0 auto;
`;

const LoginForm = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const InputField = styled.input`
  margin: 10px;
  padding: 5px;
  border-radius: 5px;
`;

// Use the gold color for text and border
const LoginButton = styled(Button)`
  border-radius: 25px;
  border-color: gold;
  color: gold;
  font-size: 15px;
  margin-bottom: 10px;
  width: 150px;
  text-transform: none;
`;

// Add the rotation animation
const rotateAnimation = `
  @keyframes rotate {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }
`;

const SignupForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    if (!username || !password) {
      alert('Please fill in all fields.');
      return;
    }
    console.log(`Signing in with username: ${username}, password: ${password}`);
  };

  return (
    <LoginContainer>
      <Link to='/admin'>
        <BackButton src={back} alt="Back" />
      </Link>
      <h1 className="school-title">ParCIT</h1>
      <Logo src={logo1} alt="Logo" />
      <LoginForm>
        <label>
          Username:
          <InputField
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </label>
        <label>
          Password:
          <InputField
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <Link to='/parkingspaces'>
          <LoginButton variant='outlined' onClick={handleLogin}>
            Log in
          </LoginButton>
        </Link>
      </LoginForm>
    </LoginContainer>
  );
};

export default SignupForm;
